function Map(){this.keyArray=new Array();this.valArray=new Array();this.put=put;this.get=get;this.size=size;this.clear=clear;this.keySet=keySet;this.valSet=valSet;this.showMe=showMe;this.findIt=findIt}function put(key,val){var elementIndex=this.findIt(key);if(elementIndex==(-1)){this.keyArray.push(key);this.valArray.push(val)}else{this.valArray[elementIndex]=val}}function get(key){var result=null;var elementIndex=this.findIt(key);if(elementIndex!=(-1)){result=this.valArray[elementIndex]}return result}function size(){return(this.keyArray.length)}function clear(){for(var i=0;i<this.keyArray.length;i++){this.keyArray.pop();this.valArray.pop()}}function keySet(){return(this.keyArray)}function valSet(){return(this.valArray)}function showMe(){var result="";for(var i=0;i<this.keyArray.length;i++){result+="Key: "+this.keyArray[i]+"\tValues: "+this.valArray[i]+"\n"}return result}function findIt(key){var result=(-1);for(var i=0;i<this.keyArray.length;i++){if(this.keyArray[i]==key){result=i;break}}return result};